// Cria um elemento script e atribui o arquivo instafeed.min.js
var instafeedScript = document.createElement('script');
instafeedScript.src = 'js/instafeed.min.js';
document.head.appendChild(instafeedScript);

// Cria um elemento link e atribui o arquivo owl.carousel.min.css
var owlCarouselCSS = document.createElement('link');
owlCarouselCSS.rel = 'stylesheet';
owlCarouselCSS.href = 'css/owl.carousel.min.css';
document.head.appendChild(owlCarouselCSS);

// Cria um elemento link e atribui o arquivo owl.theme.default.min.css
var owlThemeCSS = document.createElement('link');
owlThemeCSS.rel = 'stylesheet';
owlThemeCSS.href = 'css/owl.theme.default.min.css';
document.head.appendChild(owlThemeCSS);

// Cria um elemento div e atribui o ID instafeed e classes de carrossel
var instafeedDiv = document.createElement('div');
instafeedDiv.id = 'instafeed';
instafeedDiv.className = 'owl-carousel owl-theme owl-loaded owl-drag';
document.body.appendChild(instafeedDiv);

// Cria um objeto Instafeed
var feed = new Instafeed({
    accessToken: 'IGQVJYRHdZAaDhFTE5HbUFDZA3RtVzJSdlJIbmZAnODdsckZA6NFlpajdQdEc2QTE3a0FSQ3BKMVNTMjVaVVhrZA3BINzNoV3NMY3poak9xWXZADVHNicG9RdHVzaFFOSVhTd1RaUXN6ejgxNFJHTDdaSUlqbwZDZD',
    limit: 8,
    template:'<div class="item"><a href="{{link}}" target="_blank"><img title="{{caption}}" src="{{image}}" /></a></div>',

    // Configuração do carrossel depois que os dados são carregados
    after: function(){

        ('.owl-carousel').owlCarousel({
            loop:true,
            margin:10,
            nav:false,
            responsiveClass:true,
            responsive:{
                0:{
                    items:1
                    
                },
                600:{
                    items:3
                    
                },
                1000:{
                    items:5,                            
                    loop:false
                }
            }
        });
    }
});

// Inicia o feed
feed.run();
